// FlowClip Hotel Session Processing
const webhookData = $json;

console.log('FlowClip Hotel Research Triggered:', webhookData);

// Extract hotel data from FlowClip session
const sessionId = webhookData.sessionId || 'unknown';
const sessionType = webhookData.sessionType || 'hotel_research';
const itemCount = webhookData.itemCount || 0;
const hotels = webhookData.hotelData?.hotelNames || [];
const locations = webhookData.hotelData?.locations || [];
const dates = webhookData.hotelData?.checkInDates || [];

// Process the data
const result = {
  sessionId: sessionId,
  sessionType: sessionType,
  itemCount: itemCount,
  hotels: hotels,
  primaryLocation: locations[0] || 'Unknown Location',
  allLocations: locations,
  checkInDates: dates,
  processingTime: new Date().toISOString(),
  status: 'processing',
  message: `Processing ${itemCount} hotel items for ${locations[0] || 'unknown location'}`
};

console.log('Processed hotel data:', result);

return result;

// Generate Hotel Research Results
