// FlowClip Academic Research Processing - NO MOCK DATA
const data = $json;

console.log('ðŸ“š FlowClip Academic Research Triggered!', data);

// Extract actual academic information from session
const topics = data.academicData?.researchTopics || [];
const papers = data.academicData?.paperTitles || [];
const authors = data.academicData?.authors || [];
const keywords = data.academicData?.keywords || [];
const institutions = data.academicData?.institutions || [];
const itemCount = data.itemCount || 0;

// Return only actual session data with no mock content
const result = {
  sessionId: data.sessionId,
  sessionType: data.sessionType,
  itemCount: itemCount,

  academicData: {
    topics: topics,
    papers: papers,
    authors: authors,
    keywords: keywords,
    institutions: institutions,
    topicCount: topics.length,
    paperCount: papers.length,
    authorCount: authors.length
  },

  researchComplete: true,
  completedAt: new Date().toISOString(),

  // Only include insights if actual data was found
  insights: topics.length > 0 ? [`Found ${topics.length} research topics and ${papers.length} papers`] : ['No academic data found in session'],

  automation: {
    type: 'Academic Research Automation',
    triggeredBy: 'FlowClip Session Threshold',
    itemThreshold: 4,
    actualItems: itemCount,
    hasData: topics.length > 0 || papers.length > 0
  }
};

console.log('ðŸŽ¯ Academic research automation completed with actual data only:', result);

return result;
