// FlowClip General Research Processing - NO MOCK DATA
const data = $json;

console.log('ðŸ” FlowClip General Research Triggered!', data);

// Extract actual research information from session
const topics = data.generalData?.researchTopics || [];
const entities = data.generalData?.namedEntities || [];
const questions = data.generalData?.questions || [];
const facts = data.generalData?.factualClaims || [];
const itemCount = data.itemCount || 0;

// Return only actual session data with no mock content
const result = {
  sessionId: data.sessionId,
  sessionType: data.sessionType,
  itemCount: itemCount,

  researchData: {
    topics: topics,
    entities: entities,
    questions: questions,
    facts: facts,
    topicCount: topics.length,
    entityCount: entities.length,
    questionCount: questions.length
  },

  researchComplete: true,
  completedAt: new Date().toISOString(),

  // Only include insights if actual data was found
  insights: topics.length > 0 ? [`Found ${topics.length} topics and ${entities.length} entities to research`] : ['No research data found in session'],

  automation: {
    type: 'General Research Automation',
    triggeredBy: 'FlowClip Session Threshold',
    itemThreshold: 3,
    actualItems: itemCount,
    hasData: topics.length > 0 || entities.length > 0
  }
};

console.log('ðŸŽ¯ General research automation completed with actual data only:', result);

return result;
