// FlowClip Hotel Research Processing - NO MOCK DATA
const data = $json;

console.log('ðŸ¨ FlowClip Hotel Research Triggered!', data);

// Extract actual hotel information from session
const hotels = data.hotelData?.hotelNames || [];
const location = data.hotelData?.locations?.[0] || '';
const itemCount = data.itemCount || 0;
const dates = data.hotelData?.checkInDates || [];

// Return only actual session data with no mock content
const result = {
  sessionId: data.sessionId,
  sessionType: data.sessionType,
  itemCount: itemCount,

  hotelData: {
    hotels: hotels,
    location: location,
    checkInDates: dates,
    hotelCount: hotels.length
  },

  researchComplete: true,
  completedAt: new Date().toISOString(),

  // Only include insights if actual data was found
  insights: hotels.length > 0 ? [`Found ${hotels.length} hotels in ${location}`] : ['No hotel data found in session'],

  automation: {
    type: 'Hotel Research Automation',
    triggeredBy: 'FlowClip Session Threshold',
    itemThreshold: 2,
    actualItems: itemCount,
    hasData: hotels.length > 0 || location.length > 0
  }
};

console.log('ðŸŽ¯ Hotel research automation completed with actual data only:', result);

return result;
