// FlowClip Product Research Processing - NO MOCK DATA
const data = $json;

console.log('ðŸ›’ FlowClip Product Research Triggered!', data);

// Extract actual product information from session
const products = data.productData?.productNames || [];
const categories = data.productData?.categories || [];
const brands = data.productData?.brands || [];
const specs = data.productData?.specifications || [];
const itemCount = data.itemCount || 0;

// Return only actual session data with no mock content
const result = {
  sessionId: data.sessionId,
  sessionType: data.sessionType,
  itemCount: itemCount,

  productData: {
    products: products,
    categories: categories,
    brands: brands,
    specifications: specs,
    productCount: products.length,
    categoryCount: categories.length,
    brandCount: brands.length
  },

  researchComplete: true,
  completedAt: new Date().toISOString(),

  // Only include insights if actual data was found
  insights: products.length > 0 ? [`Found ${products.length} products for research`] : ['No product data found in session'],

  automation: {
    type: 'Product Research Automation',
    triggeredBy: 'FlowClip Session Threshold',
    itemThreshold: 3,
    actualItems: itemCount,
    hasData: products.length > 0 || categories.length > 0
  }
};

console.log('ðŸŽ¯ Product research automation completed with actual data only:', result);

return result;
